var fs = require("fs");

function start(source,target) {
   var rs=fs.createReadStream(source);
   var ws=fs.createWriteStream(target);
   
   /*
     1. 读流.pipe(写流)
   */
   rs.pipe(ws);

   /*
     2. 读流.pipe(写流)
   */
   
   /*
   rs.on('data',function(chunk){
      ws.write(chunk);
   });
   */

}

exports.start=start; 
